mapper.select:
select a.hearts from hearts h join article a on articleId = article_articleId
where article_articleId = #{article_articleId}
and login_loginNo = #{login_loginNo}

select count(*) from hearts
where article_articleId = #{article_articleId}
and login_loginNo = #{login_loginNo}

mapper.delete:
delete from hearts
where article_articleId = #{article_articleId}
and login_loginNo = #{login_loginNo}
