mapper.select:
select a.articleId, a.title, a.content, a.tags, a.published, a.source, a.views, a.hearts, a.image, a.login_loginNo, a.articlecategories_categoriesNo, b.name 
from article a join articleCategories b on b.categoriesNo = a.articlecategories_categoriesNo
where articleId = #{articleId}
order by published desc;


		select a.articleId, a.title, a.content, a.tags, a.published, a.source, a.views, a.hearts, a.image, a.login_loginNo, a.articlecategories_categoriesNo, b.name 
		from article a join articleCategories b on b.categoriesNo = a.articlecategories_categoriesNo
		where articleId = #{articleID}

        order by published desc   
        limit #{pageStart}, #{perPageNum}
